package estructuras_basicas;

public class BucleFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Bucle for para que nos imprima nuestro nombre 10 veces
		
		for (int i = 0; i < 10; i++) { // inicializar variable de contador (i), establecer la condición (i < 10) y decir en cuanto queremos que se incremetne cada iteración (i++)
			
			System.out.println("Empezando iteración nº " + (i+1));
			
			System.out.println("Miguel");
		}

	}

}
